import React,{useState,useEffect} from 'react'
import Fetch from './Fetch'
import './Data.css'

function Data() {


const [data,setdata]=useState([])
const [id,set_id]=useState()
const [first_name,set_first_name]=useState("")
const [last_name,set_last_name]=useState("")
const [switch_effect,set_switch_effect]=useState(false)


useEffect(()=>{
    fetchdata()
},[switch_effect])


async function fetchdata()
{
    await Fetch.getdata().then(res=>{
        setdata(res)
    })
    .catch((err)=>{console.log(err)})
}



function Editdata(id)
{
    // set_first_name(a)
    // set_last_name(b)
    // set_id(c)
    var specific_data=data[id-1]
    alert(JSON.stringify(specific_data.first_name))
}

function updateinfo()
{
    const values={first_name,last_name,id}
    fetch("/update/"+id,
    {
        method:"PATCH",
        headers:
        {
            'Accept':'application/json',
            'Content-Type':'application/json'
        },
        body:JSON.stringify(values)
    }
    ).then(answer=>{answer.json().then(resp=>{console.log(resp)})})
    set_switch_effect(!switch_effect)
    set_first_name("")
    set_last_name("")
}

  return (
    <div className='data'>
    
        <table>
        <tbody>
            <tr>

            <th>First Name</th>
            <th>Last Name</th>
            <th>Date</th>
            <th>Rank</th>
            <th>Gender</th>
            <th>Actions</th>
            </tr>

            {
                data?(data.map((val)=>(
                    <tr key={val._id}>
                        <td>{val.first_name}</td>
                        <td>{val.last_name}</td>
                        <td>{val.date.substring(0, 10).split('-').reverse().join('-')}</td>
                        <td>{val.rank}</td>
                        <td>{val.gender}</td>
                        <td><button style={{cursor:"pointer"}} onClick={()=>{Editdata(val._id)}}>EDIT</button>
                        <button>DELETE</button></td>
                    </tr>
                ))):<></>
            }
            </tbody>
        </table>

        <hr/>
        first name<input value={first_name} onChange={(e)=>{set_first_name(e.target.value)}}/>
        <br/>
        last name<input value={last_name} onChange={(e)=>{set_last_name(e.target.value)}}/>
        <br/>
        <button onClick={updateinfo}>Update</button>
    </div>
  )
}

export default Data
